mapper.delete:
DELETE FROM chat_messages
WHERE session_id = #{sessionId}
  AND user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM chat_messages
WHERE session_id = #{sessionId}
  AND user_id = #{userId}

SELECT COUNT(*)
FROM chat_messages
WHERE room_id = #{roomId}
  AND user_id = #{userId}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM TB_QUES_HIS
WHERE CNVS_IDT_ID = #{roomId}
  AND USR_ID = #{userId}
LIMIT 1
